import{_ as o,c as r,o as l,j as a,a0 as i,a as e}from"./chunks/framework.DXI-tbPr.js";const A=JSON.parse('{"title":"Apple Watch-AnyGo解锁地区限制功能","description":"通过 AnyGo 模拟定位，在国行 Apple Watch 上开通睡眠呼吸暂停、ECG 心电图、房颤提醒等地区限制功能的操作指南","frontmatter":{"title":"Apple Watch-AnyGo解锁地区限制功能","description":"通过 AnyGo 模拟定位，在国行 Apple Watch 上开通睡眠呼吸暂停、ECG 心电图、房颤提醒等地区限制功能的操作指南","date":"2026-03-01T00:00:00.000Z","tags":["Apple Watch"]},"headers":[],"relativePath":"posts/Apple Watch-AnyGo解锁地区限制功能.md","filePath":"posts/Apple Watch-AnyGo解锁地区限制功能.md","lastUpdated":1772335534000}'),n={name:"posts/Apple Watch-AnyGo解锁地区限制功能.md"};function h(s,t,d,p,c,b){return l(),r("div",null,[...t[0]||(t[0]=[a("blockquote",null,[a("p",null,[e("参考来源："),a("a",{href:"https://www.v2ex.com/t/1159981",target:"_blank",rel:"noreferrer"},"V2EX - 国行 Watch 开通房颤和睡眠呼吸暂停")])],-1),a("h3",{id:"参考视频",tabindex:"-1"},[e("参考视频 "),a("a",{class:"header-anchor",href:"#参考视频","aria-label":'Permalink to "参考视频"'},"​")],-1),a("div",{class:"video-container"},[a("iframe",{src:"https://player.bilibili.com/player.html?isOutside=true&aid=115243720115660&bvid=BV1rKJZzAE9W&cid=32551733015&p=1",scrolling:"no",border:"0",frameborder:"no",framespacing:"0",allowfullscreen:"true"})],-1),i('<h1 id="apple-watch-使用-anygo-解锁睡眠呼吸暂停、心电图、房颤功能" tabindex="-1">Apple Watch 使用 AnyGo 解锁睡眠呼吸暂停、心电图、房颤功能 <a class="header-anchor" href="#apple-watch-使用-anygo-解锁睡眠呼吸暂停、心电图、房颤功能" aria-label="Permalink to &quot;Apple Watch 使用 AnyGo 解锁睡眠呼吸暂停、心电图、房颤功能&quot;">​</a></h1><h2 id="_1-前提条件" tabindex="-1">1. 前提条件 <a class="header-anchor" href="#_1-前提条件" aria-label="Permalink to &quot;1. 前提条件&quot;">​</a></h2><ul><li><strong>设备</strong>：iPhone + Apple Watch（手表本身需支持 ECG、睡眠呼吸暂停、房颤等功能）</li><li><strong>作者环境</strong>：美版 Apple Watch Ultra 2 + iPhone，系统均为 OS 26</li><li><strong>软件</strong>：iToolab AnyGo（<a href="https://itoolab.com/ad/g/gps-simulator/" target="_blank" rel="noreferrer">官网</a>）</li><li><strong>网络</strong>：电脑和手机都需开启梯子，否则 AnyGo 可能提示「当前地区不可用」</li></ul><h3 id="anygo-支持的平台" tabindex="-1">AnyGo 支持的平台 <a class="header-anchor" href="#anygo-支持的平台" aria-label="Permalink to &quot;AnyGo 支持的平台&quot;">​</a></h3><table tabindex="0"><thead><tr><th>平台</th><th>版本要求</th></tr></thead><tbody><tr><td>Windows</td><td>11 / 10 / 8 / 7</td></tr><tr><td>macOS</td><td>10.12 及以上</td></tr><tr><td>iOS</td><td>iOS 13 ~ iOS 26</td></tr><tr><td>Android</td><td>Google Play 下载，支持 Android 15</td></tr></tbody></table><p>官网支持 <strong>Wi-Fi 无线</strong> 与 <strong>蓝牙</strong> 两种虚拟 GPS 连接方式。</p><blockquote><p>Windows 端目前暂无支持蓝牙模式的学习版，但正版 AnyGo 支持。若需蓝牙模式，请使用正版。</p></blockquote><hr><h2 id="_2-操作步骤-以-macos-iphone-为例" tabindex="-1">2. 操作步骤（以 macOS + iPhone 为例） <a class="header-anchor" href="#_2-操作步骤-以-macos-iphone-为例" aria-label="Permalink to &quot;2. 操作步骤（以 macOS + iPhone 为例）&quot;">​</a></h2><ol><li><strong>全程关闭 Apple Watch</strong>，操作期间保持关机</li><li><strong>连接</strong>：使用 AnyGo 蓝牙模式连接 iPhone（或用数据线连接电脑）</li><li><strong>按 AnyGo 软件提示操作</strong>： <ul><li>关闭手机定位服务（设置 → 隐私与安全性 → 定位服务）</li><li>关闭 Wi-Fi、蜂窝数据（可以直接飞行模式）</li><li>等待约 10 秒</li><li>再依次重新打开（wifi，蜂窝数据，手机定位）</li></ul></li><li><strong>手机开启全局代理</strong>，节点选择香港等支持地区</li><li><strong>验证定位</strong>：用百度地图或高德地图确认定位已变更为目标地区</li><li><strong>开通功能</strong>：打开「健康」App，按提示开通睡眠呼吸暂停、ECG、房颤等</li><li><strong>关于房颤历史</strong>：若与 ECG 只能二选一，按需选择其一</li></ol><hr><h2 id="_3-常见问题" tabindex="-1">3. 常见问题 <a class="header-anchor" href="#_3-常见问题" aria-label="Permalink to &quot;3. 常见问题&quot;">​</a></h2><h3 id="蓝牙连接失败" tabindex="-1">蓝牙连接失败 <a class="header-anchor" href="#蓝牙连接失败" aria-label="Permalink to &quot;蓝牙连接失败&quot;">​</a></h3><p>部分用户反馈：删除 <code>~/Library/Preferences/</code> 下 <code>com.apple.bluetooth</code> 和 <code>com.apple.bluetoothuserd</code> 开头的 plist 文件，重启 Mac 后，在蓝牙设置中「忽略」原设备，再重新配对，可解决部分连接问题。</p><h3 id="学习版蓝牙连不上" tabindex="-1">学习版蓝牙连不上 <a class="header-anchor" href="#学习版蓝牙连不上" aria-label="Permalink to &quot;学习版蓝牙连不上&quot;">​</a></h3><p>原帖回帖中，有人用学习版可成功，有人需换成正版 AnyGo 才能连上蓝牙。可能与环境有关，建议多试几次或考虑正版。</p><h3 id="定位改好了-健康-app-仍提示地区不可用" tabindex="-1">定位改好了，健康 App 仍提示地区不可用 <a class="header-anchor" href="#定位改好了-健康-app-仍提示地区不可用" aria-label="Permalink to &quot;定位改好了，健康 App 仍提示地区不可用&quot;">​</a></h3><p>检查代理是否全局、定位是否真的生效。部分用户使用有线连接代替蓝牙后成功。</p><hr><h2 id="_4-重要说明" tabindex="-1">4. 重要说明 <a class="header-anchor" href="#_4-重要说明" aria-label="Permalink to &quot;4. 重要说明&quot;">​</a></h2><h3 id="成功率较玄学" tabindex="-1">成功率较玄学 <a class="header-anchor" href="#成功率较玄学" aria-label="Permalink to &quot;成功率较玄学&quot;">​</a></h3><p>本教程成功率不稳定，初步怀疑与 AnyGo 版本、正版/学习版有关。原帖中有不同系统（iOS 18.x、26.x，macOS 15.x、26.x 等）成功案例，目前倾向于与系统版本关系不大。<strong>教程仅供参考，不保证成功。</strong></p><h3 id="代开服务风险" tabindex="-1">代开服务风险 <a class="header-anchor" href="#代开服务风险" aria-label="Permalink to &quot;代开服务风险&quot;">​</a></h3><p>若选择某宝代开：需登录他人 Apple ID，存在隐私泄露风险，且可能同步大量无关数据，请谨慎选择。可能会直接被别人的ID锁住，导致被诈骗风险</p>',24)])])}const g=o(n,[["render",h]]);export{A as __pageData,g as default};
